import React, { Component } from 'react'

export default class Contador extends Component {
    maisUm = () => {
        this.props.numero++
    }
    render() {
        return (
            <div>
                <div>Numero: {this.props.numero} </div>
                <button onClick={this.maisUm}>Inc</button>
                {/* Solução 2 <button onClick={() => this.maisUm()}>Inc</button> */}
                <button>Dec</button>
            </div>
        )
    }
}
